import anime from 'animejs'
import { resetAnimation } from './utils'


async function runSequence(sequence) {
    const timeline = anime.timeline({
        duration: 250,
        easing: 'easeOutExpo',
        complete: resetAnimation()
    })
    for (const seq of sequence) {
        timeline.add(seq)
    }
    return timeline
}

export default runSequence
