import anime from 'animejs'
import {resetAnimation} from './utils'


function runSequence(sequence) {
    const timeline = anime.timeline({
        duration: 250,
        easing: 'easeOutExpo'
    })
    for (const seq of sequence) {
        timeline.add(seq)
    }
    resetAnimation()
    return timeline
}

export default runSequence
